		{
			try
			{
				if (TryAgainButton.Visible == true)
				{
					m_MusicPlayer.controls.stop();
					m_MusicPlayer.URL = "TryAgain.mp3";
					m_MusicPlayer.settings.playCount = 100;
					m_MusicPlayer.controls.play();
					m_MusicPlayer.settings.volume = 100;
					TryAgainGif.Visible = true;
					TryAgainGif1.Visible = true;
					TryAgainGif2.Visible = true;
					MusicPlayButton.Visible = false;
					MusicStopButton.Visible = false;
					DanceGif.Visible = false;
					DanceGif2.Visible = false;
					DanceGif3.Visible = false;
				}
				else if ((TryAgainButton.Visible == true)&& (Correct_AnswerScoreBox.Text == "78" ))
				{
					m_MusicPlayer.controls.stop();
					m_MusicPlayer.URL = "TryAgain.mp3";
					m_MusicPlayer.settings.playCount = 100;
					m_MusicPlayer.controls.play();
					m_MusicPlayer.settings.volume = 100;
					Perfect_Gif.Visible = true;
					MusicPlayButton.Visible = false;
					MusicStopButton.Visible = false;
					DanceGif.Visible = false;
					DanceGif2.Visible = false;
					DanceGif3.Visible = false;
					TryAgainButton.Visible = false;
